inString = ""

def isPalindrome(data):
    other = list(data[::-1])
    for char in data:
        if char != other.pop(0):
            return False;
    return True;

best = ""

for i in range(0, len(inString)):
    for j in range(0, len(inString)):
        current = inString[i:j]
        if isPalindrome(current):
            if len(current) > len(best):
                best = current


